package com.syntax.class01;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// storing and representing whole numbers data types
		byte box1 = 20;
		short box2 = 100;
		int box3 = 100000;  // most popular data type
		long box4 = 1000000000000l;  // credit cards, transaction id, tracking number
		
		// storing and representing decimals data types
		float variable1 = 1.99f;
		double variable2 = 99.00; //popular
		
		// storing single characters
		char container = 'A';
		char dollar = '$';
		
		// storing yes or no characters
		boolean hot = true;
		boolean tired = false;
		
		System.out.println(box1);
		System.out.println(box2);
		System.out.println(box3);
		System.out.println(box4);
		System.out.println(variable1);
		System.out.println(variable2);
		System.out.println(container);
		System.out.println(dollar);
		System.out.println(hot);
		System.out.println(tired);
	}

}
