package com.syntax.class02;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "Talha"; 
		String lastname = "Riaz";
		String grade= "A";
		String city= "Wah";
		String state= "Islamabad";
		String phonenumber= "03440548100";
		System.out.println("My name is " + name + " and my last name is " + lastname);
		System.out.println("I am a " + grade + " student");
		System.out.println("I live in city " + city + " and state is " + state);
		System.out.println("And my phone number is " + phonenumber);
		city = "taxila";
		state = "Rawalpindi";
		phonenumber = "03335443033";
		grade = "B";
		System.out.println("My name is " + name + " and my last name is " + lastname);
		System.out.println("I am a " + grade + " student");
		System.out.println("I moved to a new city " + city + " and new state is " + state);
		System.out.println("And my new phone number is " + phonenumber);
		
		
		
	}

}
