package replitTasks;

public class Task11ArithmeticOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*  **For you to do:**
		Write a program to print the perimeter and area of a rectangle with width = 5 and height = 8. 
		**Output:**
		"The perimeter of a rectangle with width __ and height __ is equals to __ and the area equals to __"   */
		
		int width = 5;
		int height = 8;
		int perimeter = 2*(width+height);
		int area = width * height;
		
		System.out.print("The perimeter of a rectangle with width " + width + " and height " + height + " is equals to " + perimeter + " and the area equals to " + area);
		

	}

}
